import React from 'react';
import { motion } from 'framer-motion';
import Home from './Home';
import About from './About';
import Experience from './Experience';
import Education from './Education';
import Skills from './Skills';
import Contact from './Contact';
import './Dashboard.css';

const Dashboard = () => {
  const sections = [
    { id: 'home', component: Home, title: 'Home' },
    { id: 'about', component: About, title: 'About' },
    { id: 'experience', component: Experience, title: 'Experience' },
    { id: 'education', component: Education, title: 'Education' },
    { id: 'skills', component: Skills, title: 'Skills' },
    { id: 'contact', component: Contact, title: 'Contact' }
  ];

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="dashboard">
      {/* Navigation Dots */}
      <div className="dashboard-nav">
        {sections.map((section, index) => (
          <motion.button
            key={section.id}
            className="nav-dot"
            onClick={() => scrollToSection(section.id)}
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <span className="dot"></span>
            <span className="dot-label">{section.title}</span>
          </motion.button>
        ))}
      </div>

      {/* Main Content */}
      <div className="dashboard-content">
        {sections.map((section, index) => (
          <motion.div
            key={section.id}
            id={section.id}
            className="dashboard-section"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: index * 0.2 }}
            viewport={{ once: true }}
          >
            <div className="section-container">
              <motion.h2
                className="section-title"
                initial={{ opacity: 0, x: -50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
                viewport={{ once: true }}
              >
                {section.title}
              </motion.h2>
              <div className="section-content">
                <section.component />
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
